import aoc.AocRunner
import aoc.allDistinctCharacters

val test = "nppdvjthqldpwncqszvftbrmjlhg"
val input="""

""".trim()

AocRunner(6,
    info = { listOf("Leaderboard: 1:52/2:25", "Answers: 1093/3534") },
    test = { listOf(
        "(Test4) First distinct 4 after ${firstDistinctSubstring(test, 4)} characters",
        "(Test14) First distinct 14 after ${firstDistinctSubstring(test, 14)} characters")
    },
    part1 = { firstDistinctSubstring(input, 4) },
    part2 = { firstDistinctSubstring(input, 14) }
).run()

fun firstDistinctSubstring(input: String, n: Int) = (n..input.length).first {
    input.substring(it - n, it).allDistinctCharacters()
}